    /**
     * 执行打卡
     *
     * @param
     * @return
     */
    public String updateMi(MiSportVo mi) {
        String usernameType = "huami_phone";
        String account = mi.getUin();
        boolean mobile = Validator.isMobile(account);
        boolean email = Validator.isEmail(account);
        if (mobile) {
            account = "+86" + account;
        } else if (email) {
            usernameType = "email";
        } else {
            ErrorCode errorCode = new ErrorCode(1011000033, "请输入手机号或者邮箱");
            throw new ServiceException(errorCode);
        }
        MiCardDO card = null;
        if (mi.getModel()) {
            card = miCardMapper.selectOne(new AppMiCardExportReqVO().setCardNo(mi.getCardNo()).setStatus(1));
            if (Objects.isNull(card)) {
                throw new ServiceException(ErrorCodeConstants.MI_CARD_NOT_EXISTS);
            }
        }

        String accessCode = this.getAccessCode(account, mi.getPwd());
        if (accessCode == null || accessCode.equals("")) {
            ErrorCode errorCode = new ErrorCode(1011000032, StrUtil.format("当前账号：{}，打卡失败，请检查账号密码是否正确", mi.getUin()));
            throw new ServiceException(errorCode);
        }

        Map<String, String> login = login(accessCode, usernameType);
        String login_token = login.get("login_token");
        String user_id = login.get("user_id");
        String appToken = getAppToken(login_token);
        String time = getTime();
        if (StrUtil.isBlank(appToken) || StrUtil.isBlank(user_id)) {
            ErrorCode errorCode = new ErrorCode(1011000033, StrUtil.format("当前账号：{}，打卡失败，请检查账号密码是否正确", mi.getUin()));
            throw new ServiceException(errorCode);
        }

        //上面登录校验已完成,账号正常可用
        //自动化 把计划存入表中,待每半小时的定时任务扫描后执行.
        if (mi.getModel()) {
            //根据卡密计算得出 过期时间
            card.setStatus(2);
            miCardMapper.updateById(card);
            LocalDateTime endTime;
            Integer type = card.getType();
            switch (type) {
                case 1:
                    endTime = LocalDateTime.now().plusDays(8);
                    break;
                case 2:
                    endTime = LocalDateTime.now().plusDays(366);
                    break;
                //永久
                case 3:
                    endTime = LocalDateTime.now().plusYears(99);
                    break;
                default:
                    throw new ValidationException("参数异常");
            }

            mi.getZdList().forEach(zdVo -> {
                String[] split = zdVo.getTime().split(":");
                if (split != null && split.length != 2) {
                    return;
                }
                MiJobDO job = new MiJobDO();
                StringBuffer sb = new StringBuffer();
                sb.append("* ").append(split[1]).append(" ").append(split[0]).append(" * * ?");

                // 调用XXLJOBUTIL去新增定时任务
                MiSportVo miSportVo = new MiSportVo();
                miSportVo.setUin(mi.getUin());
                miSportVo.setPwd(mi.getPwd());
                miSportVo.setMaxStep(zdVo.getZdMaxStep());
                miSportVo.setMinStep(zdVo.getZdMinStep());
                miSportVo.setModel(false);
                miSportVo.setSource("bbs");

                //改成新增定时任务
//                XxlJobInfo xxlJobInfo = new XxlJobInfo();
//                xxlJobInfo.setJobGroup(2);
//                xxlJobInfo.setJobDesc("test");
//                xxlJobInfo.setAuthor("admin");
//                xxlJobInfo.setAlarmEmail("");
//                xxlJobInfo.setScheduleType("CRON");
//                xxlJobInfo.setScheduleConf(sb.toString());
//                xxlJobInfo.setGlueType("BEAN");
//                xxlJobInfo.setExecutorHandler("miJobHandler");
//                xxlJobInfo.setExecutorParam(JSON.toJSONString(miSportVo));
//                // xxlJobInfo.setExecutorParam("{\"uin\":\"11111111111\",\"pwd\":\"peiqi666\",\"minStep\": 100,\"maxStep\": 200,\"source\":\"xxl\",\"model\":\"false\"}");
//                xxlJobInfo.setExecutorRouteStrategy("FIRST");
//                xxlJobInfo.setMisfireStrategy("DO_NOTHING");
//                xxlJobInfo.setExecutorBlockStrategy("SERIAL_EXECUTION");
//                //新增任务并启动
//                Integer jobId = xxlJobComponent.add(xxlJobInfo);
//                xxlJobComponent.start(jobId);
                //todo 关联用户id
                job.setCron(sb.toString()).setMobile(mi.getUin()).setPwd(mi.getPwd())
                        .setCardNo(mi.getCardNo()).setEndTime(endTime)
                        .setMaxStep(zdVo.getZdMaxStep()).setMinStep(zdVo.getZdMinStep())
                        .setJobId(1);//todo
                miJobMapper.insert(job);

            });

            return StrUtil.format("当前账号：{}提交自动打卡成功，时间为：{}", mi.getUin(), DateUtil.now());
        } else {
            //手动模式
            //随机 10000<=stepNum<20001 的数 不包括20001
            int stepNum = ThreadLocalRandom.current().nextInt(mi.getMinStep(), mi.getMaxStep() + 1);
            updateStep(appToken, user_id, time, stepNum);

            MiLogDO log = new MiLogDO();
            log.setMobile(mi.getUin()).setPwd(mi.getPwd()).setSource(mi.getSource()).setMaxStep(mi.getMaxStep())
                    .setMinStep(mi.getMinStep()).setRealStep(stepNum);
            miLogMapper.insert(log);
            return StrUtil.format("当前账号：{}打卡成功，步数为：{}，打卡时间为：{}", mi.getUin(), stepNum, DateUtil.now());

        }


    }


    //获取oauth 码
    public String getAccessCode(String account, String password) {

        try {
            URIBuilder builder = new URIBuilder("https://api-user.huami.com/registrations/" + account + "/tokens");
            HashMap<String, String> data = new HashMap<>();
            data.put("client_id", "HuaMi");
            data.put("country_code", "CN");
            data.put("state", "REDIRECTION");
            data.put("password", password);
            data.put("redirect_uri", "https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html");
            data.put("token", "access");
            data.forEach(builder::setParameter);
            HttpPost httpPost = new HttpPost(builder.build());
            httpPost.setConfig(RequestConfig.custom()
                    .setSocketTimeout(5000)
                    ////默认允许自动重定向
                    .setRedirectsEnabled(false)
                    .build());
            httpPost.addHeader("User-Agent", UserAgent);
            httpPost.addHeader("Content-Type", "application/x-www-form-urlencoded;charset=UTF-8");
            HttpResponse execute = httpClient.execute(httpPost);
            int statusCode = execute.getStatusLine().getStatusCode();
            Header location = execute.getFirstHeader("Location");
            String params = location.getValue().substring(location.getValue().indexOf("?") + 1);
            Map<String, String> split = Splitter.on("&").withKeyValueSeparator("=").split(params);
            String s = split.get("access");
            return s;
        } catch (Exception e) {
            e.printStackTrace();
        }
        return null;
    }

    //登录
    public Map<String, String> login(String accessCode, String usernameType) {
        try {
            HashMap<String, String> data1 = new HashMap<>();
            data1.put("allow_registration", "false");
            data1.put("app_name", "com.xiaomi.hm.health");
            data1.put("app_version", "6.3.5");
            data1.put("code", accessCode);
            data1.put("country_code", "CN");
            data1.put("device_id", "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1");
            data1.put("device_id_type", "uuid");
            data1.put("device_model", "phone");
            data1.put("dn", "api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com");
            data1.put("grant_type", "access_token");
            data1.put("lang", "zh_CN");
            data1.put("os_version", "1.5.0");
            data1.put("source", "com.xiaomi.hm.health");
            data1.put("third_name", usernameType);
            URIBuilder builder1 = new URIBuilder("https://account.huami.com/v2/client/login");
            data1.forEach(builder1::setParameter);
            HttpPost httpPost1 = new HttpPost(builder1.build());
            httpPost1.addHeader("User-Agent", UserAgent);
            httpPost1.addHeader("Content-Type", "application/x-www-form-urlencoded;charset=UTF-8");
            HttpResponse execute1 = httpClient.execute(httpPost1);
            String s1 = EntityUtils.toString(execute1.getEntity());
            JsonNode jsonNode = objectMapper.readTree(s1);
            String login_token = jsonNode.get("token_info").get("login_token").asText();
            String user_id = jsonNode.get("token_info").get("user_id").asText();
            HashMap<String, String> map = new HashMap<>();
            map.put("login_token", login_token);
            map.put("user_id", user_id);
            return map;
        } catch (Exception e) {
            e.printStackTrace();
        }
        return null;
    }

    public String getAppToken(String login_token) {
        try {
            HttpGet httpGet = new HttpGet("https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&login_token=" + login_token);
            httpGet.addHeader("User-Agent", "Dalvik/2.1.0 (Linux; U; Android 9; MI 6 MIUI/20.6.18)");
            CloseableHttpResponse execute3 = httpClient.execute(httpGet);
            String s3 = EntityUtils.toString(execute3.getEntity());
            String app_token = objectMapper.readTree(s3).get("token_info").get("app_token").asText();
            return app_token;
        } catch (Exception e) {
            e.printStackTrace();
        }
        return null;
    }

    public String getTime() {
        try {
            HttpGet httpGet = new HttpGet("http://api.m.taobao.com/rest/api3.do?api=mtop.common.getTimestamp");
            httpGet.addHeader("User-Agent", "Dalvik/2.1.0 (Linux; U; Android 9; MI 6 MIUI/20.6.18)");
            CloseableHttpResponse execute = null;
            execute = httpClient.execute(httpGet);
            String s3 = EntityUtils.toString(execute.getEntity());
            String time = objectMapper.readTree(s3).get("data").get("t").asText();
            return time;
        } catch (IOException e) {
            e.printStackTrace();
        }
        return null;
    }

